package Java.Coding;

public class pyramid {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k = 10;
		for(int i = 1; i<=4; i++)
		{
			
			for(int j = i; j<=4; j++)
				
			{
				
				System.out.print(k + " ");
				k--;
			}
			System.out.println();
		}
		
		
	}

}
