package Java.Coding;

public class stringtextblock {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int k =1;
		for(int i = 5; i>=1; i--)
		{
			for(int j = i; j<=5; j++)
			{
				System.out.print(k +"\t");
				k++;
				
			}
			System.out.println();
			
		}
	}

}
